mapper.update:
UPDATE roomimage_tb
SET file_delete = 'Y'
WHERE roomimage_no = #{imageNo}

UPDATE roomImage_tb
SET original_name = #{refinedImage.originalName}, save_name = #{refinedImage.saveName}, created_date =#{refinedImage.createdDate}
WHERE roomimage_no = #{imageNo}

mapper.select:
select * from roomimage_tb
where room_no = #{roomNo}
and thumbnail= 'N'
and file_delete = 'N';

select * from roomimage_tb
where room_no = #{roomNo}
and thumbnail= 'Y'
and file_delete = 'N';
