mapper.select:
select a.*, IFNULL(b.cnt, 0) as cnt, IFNULL(c.created_date, '0101-01-01'), IFNULL(c.save_name, 'noImage'), IFNULL(c.original_name, 'noImage') FROM room_tb a LEFT JOIN ( SELECT room_no, COUNT(roption_no) cnt FROM roomoption_tb GROUP BY room_no ) b ON a.room_no = b.room_no LEFT JOIN ( select room_no, thumbnail, file_delete, created_date, save_name, original_name from roomimage_tb where thumbnail = 'Y' and file_delete = 'N' ) c (room_no, thumbnail, file_delete, created_date, save_name, original_name) ON a.room_no = c.room_no WHERE a.room_delete = 0 AND b.cnt != 0
AND a.room_div IN
#{roomDiv}
AND a.room_title LIKE CONCAT('%', #{keyword}, '%')
AND (
a.address LIKE CONCAT(#{location}, '%')
)
ORDER BY a.room_no DESC
LIMIT #{pagination.limitStart}, #{recordSize}

select COUNT(*) as room_count from ( select a.*, IFNULL(b.cnt, 0) as cnt FROM room_tb a LEFT JOIN ( SELECT room_no, COUNT(roption_no) cnt FROM roomoption_tb GROUP BY room_no ) b ON a.room_no = b.room_no LEFT JOIN ( SELECT * FROM roomimage_tb WHERE thumbnail = 'Y' AND file_delete = 'N' ) c ON a.room_no = c.room_no WHERE a.room_delete = 0 AND b.cnt != 0
AND a.room_div IN
#{roomDiv}
AND a.room_title LIKE CONCAT('%', #{keyword}, '%')
AND (
a.address LIKE CONCAT(#{location}, '%')
)
) as subquery_result

SELECT start_time
FROM time_reservations_tb
WHERE room_no = #{roomNo}
    AND reservation_date = #{selectDate}

select start_time
FROM time_reservations_tb
WHERE room_no = #{roomNo}
    AND reservation_date = #{selectDate}

SELECT end_time
FROM time_reservations_tb
WHERE room_no = #{roomNo}
    AND reservation_date = #{selectDate}

SELECT start_time, end_time
FROM reservation_tb
WHERE room_no = #{roomNo}
    AND roption_no = #{optionNo}
    AND reservation_date = #{selectDate}

SELECT member_no
FROM member_tb
WHERE member_id = #{memberId}

SELECT *
FROM roomimage_tb
WHERE room_no = #{roomNo}
    and file_delete = 'N';

mapper.insert:
INSERT INTO reservation_tb (
    reservation_date, reservation_creation_date, start_time, end_time, seller_no, member_no, room_no, roption_no, end_date_time
    )
VALUES (
    #{parsingDate}, now(), #{reserveStartTime}, #{reserveEndTime}, #{sellerNo}, #{memberNo}, #{roomNo}, #{optionNo}, #{endDateTime}
    )
