mapper.select:
SELECT a.room_no, a.room_title, a.room_div, a.room_content, a.postcode, a.address, a.detail_address, a.extra_address, a.room_delete, a.seller_no, IFNULL(b.cnt, 0) as option_cnt, IFNULL(c.upload_date, '0101-01-01') as upload_date, IFNULL(c.save_name, 'noImage') as save_name, IFNULL(c.original_name, 'noImage') as original_name FROM room_tb a LEFT JOIN ( SELECT room_no, COUNT(roption_no) cnt FROM roomoption_tb GROUP BY room_no ) b ON a.room_no = b.room_no LEFT JOIN ( SELECT room_no, thumbnail, file_delete, upload_date, save_name, original_name FROM roomimage_tb WHERE thumbnail = 'Y' AND file_delete = 'N' ) c ON a.room_no = c.room_no WHERE a.room_delete = 'N' AND IFNULL(b.cnt, 0) != 0
AND a.room_div IN
#{roomDiv}
AND a.room_title LIKE CONCAT('%', #{keyword}, '%')
AND (
a.address LIKE CONCAT(#{location}, '%')
)
ORDER BY a.room_no DESC
LIMIT #{pagination.limitStart}, #{recordSize}

select COUNT(*) as room_count from ( SELECT a.*, IFNULL(b.cnt, 0) as cnt FROM room_tb a LEFT JOIN ( SELECT room_no, COUNT(roption_no) cnt FROM roomoption_tb GROUP BY room_no ) b ON a.room_no = b.room_no LEFT JOIN ( SELECT * FROM roomimage_tb WHERE thumbnail = 'Y' AND file_delete = 'N' ) c ON a.room_no = c.room_no WHERE a.room_delete = 'N' AND b.cnt != 0
AND a.room_div IN
#{roomDiv}
AND a.room_title LIKE CONCAT('%', #{keyword}, '%')
AND (
a.address LIKE CONCAT(#{location}, '%')
)
) as subquery_result

SELECT room_no, room_title, room_div, room_content, postcode, address, detail_address, extra_address, seller_no
FROM room_tb
WHERE room_no = #{roomNo}

SELECT start_time
FROM time_reservations_tb
WHERE room_no = #{roomNo}
    AND reservation_date = #{selectDate}

select start_time
FROM time_reservations_tb
WHERE room_no = #{roomNo}
    AND reservation_date = #{selectDate}

SELECT end_time
FROM time_reservations_tb
WHERE room_no = #{roomNo}
    AND reservation_date = #{selectDate}

SELECT start_time, end_time
FROM reservation_tb
WHERE room_no = #{roomNo}
    AND roption_no = #{optionNo}
    AND reservation_date = #{selectDate}

SELECT member_no
FROM member_tb
WHERE member_id = #{memberId}

SELECT CASE WHEN COUNT(*) > 0 THEN true ElSE false END
FROM bookmark_tb
WHERE member_no = #{memberNo}
AND room_no = #{roomNo}

mapper.insert:
INSERT INTO reservation_tb (
    reservation_date, start_time, end_time, reservation_status, seller_no, member_no, room_no, roption_no, reservation_creationdate, reserve_end_datetime
    )
VALUES (
    #{parsingDate}, #{reserveStartTime}, #{reserveEndTime}, 1, #{sellerNo}, #{memberNo}, #{roomNo}, #{optionNo}, now(), #{endDateTime}
    )

mapper.delete:
DELETE from bookmark_tb
WHERE member_no = #{memberNo}
AND room_no = #{roomNo}
