mapper.select:
SELECT seller_no
FROM seller_tb
where seller_id = #{authId}

select rm.room_no, rm.room_title, rm.room_div, COALESCE(ro.cnt, 0) as cnt
FROM room_tb rm
LEFT JOIN (
    SELECT room_no, COUNT(*) cnt
    FROM roomoption_tb
    GROUP BY room_no
) ro ON rm.room_no = ro.room_no
WHERE rm.seller_no = #{sellerNo}
    AND rm.room_delete = 0;

SELECT COUNT(*)
FROM roomoption_tb
WHERE room_no = #{roomNo}

SELECT COUNT(*)
FROM room_tb
WHERE seller_no = #{sellerNo}

select * from roomimage_tb
where room_no = #{roomNo}
    and thumbnail= 'N'
    and file_delete = 'N';

select * from roomimage_tb
where room_no = #{roomNo}
and thumbnail= 'Y'
and file_delete = 'N';

SELECT roption_no
FROM roomoption_tb
WHERE room_no = #{roomNo}

mapper.delete:
DELETE FROM roomoptionimage_tb
WHERE roption_no IN(

    #{roomNo}

)
